function constructAlarmAddressSpace(server, addressSpace, eventObjects, done) {
  // server = the created node-opcua server
  // addressSpace = script placeholder
  // eventObjects = to hold event variables in memory from this script

  // internal global sandbox objects are 
  // node = node of the flex server, 
  // coreServer = core iiot server object for debug and access to nodeOPCUA,
  // and scriptObjects to hold variables and functions

  const opcua = coreServer.choreCompact.opcua;
  const LocalizedText = opcua.LocalizedText;
  const namespace = addressSpace.getOwnNamespace();
//  coreServer.internalDebugLog('init dynamic address space')
//  node.warn('SIMULATOR: construct new address space for OPC UA')

  const Variant = opcua.Variant;
  const DataType = opcua.DataType;
  const DataValue = opcua.DataValue;

  var flexServerInternals = this;

  this.sandboxFlowContext.set("status", 0.0);
  this.sandboxFlowContext.set("item_count_good", 0.0);
  this.sandboxFlowContext.set("item_count_bad", 0.0);
  this.sandboxFlowContext.set("prev_status_timestamp", 0.0);


  const rootFolder = addressSpace.findNode("RootFolder");

//  node.warn("SIMULATOR: construct new address space for OPC UA");

  const myDevice = namespace.addFolder(rootFolder.objects, {
  "browseName": "Simulation"
  });

  const varStatus = namespace.addVariable({
  "organizedBy": myDevice,
  "browseName": "STATUS",
  "nodeId": "ns=1;s=STATUS",
  "dataType": "Double",
  "value": {
    "get": function() {
      return new Variant({
        "dataType": DataType.Double,
        "value": flexServerInternals.sandboxFlowContext.get("status")
      });
    },
    "set": function(variant) {
      flexServerInternals.sandboxFlowContext.set(
        "status",
        parseFloat(variant.value)
      );
      return opcua.StatusCodes.Good;
    }
  }
  });

  const varItemCountGood = namespace.addVariable({
  "organizedBy": myDevice,
  "browseName": "ITEM_COUNT_GOOD",
  "nodeId": "ns=1;s=ITEM_COUNT_GOOD",
  "dataType": "Double",
  "value": {
    "get": function() {
      return new Variant({
        "dataType": DataType.Double,
        "value": flexServerInternals.sandboxFlowContext.get("item_count_good")
      });
    },
    "set": function(variant) {
      flexServerInternals.sandboxFlowContext.set(
        "item_count_good",
        parseFloat(variant.value)
      );
      return opcua.StatusCodes.Good;
    }
  }
  });

  const varItemCountBad = namespace.addVariable({
  "organizedBy": myDevice,
  "browseName": "ITEM_COUNT_BAD",
  "nodeId": "ns=1;s=ITEM_COUNT_BAD",
  "dataType": "Double",
  "value": {
    "get": function() {
      return new Variant({
        "dataType": DataType.Double,
        "value": flexServerInternals.sandboxFlowContext.get("item_count_bad")
      });
    },
    "set": function(variant) {
      flexServerInternals.sandboxFlowContext.set(
        "item_count_bad",
        parseFloat(variant.value)
      );
      return opcua.StatusCodes.Good;
    }
  }
  });

//  node.warn("SIMULATOR: construction of new address space for OPC UA done");

  done();
}

