// flow.set('isoInput1', Math.random() + 11.0) interval comes from server
var prevStatus = flow.get('status');
var prevStatusTimestamp = flow.get('prev_status_timestamp');
var curStatus;
var isOnOrOff;
var itemCountGood;
var itemCountBad;
const ChangeInterval = 600000;
const StatusValuesForOn = [101.0,105.0,108.0];
const StatusValuesForOff = [102.0,104.0,106.0,107.0,109.0];

//node.warn("prevStatus:"+prevStatus);
//node.warn("prevStatusTimestamp:"+prevStatusTimestamp);
if(prevStatus===0)
{
//  node.warn("first call");
  curStatus = StatusValuesForOn[parseInt((Math.random() * StatusValuesForOn.length-1), 10)];
  prevStatusTimestamp=Date.now();
}
else if (Date.now()-prevStatusTimestamp>ChangeInterval)
{
 isOnOrOff = (Math.random()<0.9);
 if (isOnOrOff)
    curStatus = StatusValuesForOn[parseInt((Math.random() * StatusValuesForOn.length-1), 10)];
 else
    curStatus = StatusValuesForOff[parseInt((Math.random() * StatusValuesForOff.length-1), 10)];

 prevStatusTimestamp=Date.now();
}
else
 curStatus = prevStatus;

//node.warn("curStatus:"+curStatus);

isOnOrOff = StatusValuesForOn.includes(curStatus);

//node.warn("isOnOrOff:"+isOnOrOff);
if (isOnOrOff)
{
    itemCountGood = 80.0+Math.round(Math.random()*41);
    itemCountBad = parseFloat(Math.round(Math.random()*11));
}
else
{
    itemCountGood = 0.0;
    itemCountBad = 0.0;
}

flow.set('prev_status_timestamp', prevStatusTimestamp);
flow.set('status', curStatus) ;
flow.set('item_count_good', itemCountGood);
flow.set('item_count_bad', itemCountBad);

msg.payload = [
    flow.get('status'),
    flow.get('item_count_good'),
    flow.get('item_count_bad')
]
return msg;
